#!/usr/bin/env bash

oc export bc/angular-on-nginx-build -o json -n nrts-prc-tools > angular-on-nginx-build-backup.json
oc patch --local=true -n nrts-prc-tools -f angular-on-nginx-build-backup.json --type="json" -o json --patch='[   {     "op": "replace",     "path": "/spec/source",     "value": {       "type": "Dockerfile",       "dockerfile": "FROM angular-on-nginx-build-angular-app:latest\nCOPY * /tmp/app/dist/\nCMD  /usr/libexec/s2i/run",       "images": [         {           "from": {             "kind": "ImageStreamTag",             "name": "angular-on-nginx-build-angular-app:latest"           },           "paths": [             {               "sourcePath": "/opt/app-root/src/dist/.",               "destinationDir": "tmp"             }           ]         }       ]     }   },   {     "op": "replace",     "path": "/spec/strategy",     "value": {       "type": "Docker",       "dockerStrategy": {         "from": {           "kind": "ImageStreamTag",             "name": "nginx-runtime:latest"         }       }     }   } ]' > angular-on-nginx-build-patched.json